package com.td.desafio.latam;

public class CiclosAnidados {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 10; i++) {//numero tabla de multiplicar
			for (int j = 0; j <= 10; j++) {
				System.out.println(i + " x " +j+ " = "+ (i*j));				
			}
			System.out.println("***************");
		}
	}

}
